import React, { useState, useRef, useEffect } from "react";
import { query } from "./api";

/**
 * Componente principal del chat RAG
 * Permite hacer preguntas al sistema y recibir respuestas con fuentes
 */
export default function Chat({ onLogout }) {
  // Estados del componente de chat
  const [question, setQuestion] = useState("");
  const [loading, setLoading] = useState(false);
  const [answer, setAnswer] = useState("");
  const [sources, setSources] = useState([]);
  const [conversationId, setConversationId] = useState(null);
  const [error, setError] = useState("");

  // Referencia para hacer scroll automático al final del chat
  const endRef = useRef(null);

  /**
   * Efecto para hacer scroll automático cuando hay nueva respuesta
   */
  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [answer, sources, loading]);

  /**
   * Función para enviar pregunta al sistema RAG
   * Procesa la respuesta y muestra las fuentes
   */
  async function handleSendQuery() {
    if (!question.trim()) return;

    setLoading(true);
    setError("");
    
    try {
      // Enviar pregunta al backend Django
      const response = await query(question, 3, conversationId);
      
      // Actualizar estado con la respuesta
      setAnswer(response.answer);
      setSources(response.sources || []);
      
      // Guardar ID de conversación para mantener contexto
      if (response.conversation_id) {
        setConversationId(response.conversation_id);
      }
      
      // Limpiar campo de pregunta después del envío exitoso
      setQuestion("");
      
    } catch (err) {
      // Manejar errores de la consulta
      if (err.response?.status === 401) {
        setError("Sesión expirada. Por favor inicia sesión nuevamente.");
        // Opcional: redirigir al login automáticamente
        setTimeout(() => onLogout && onLogout(), 3000);
      } else {
        setError("Error al procesar la consulta. Intenta nuevamente.");
      }
    } finally {
      setLoading(false);
    }
  }

  /**
   * Maneja la tecla Enter para enviar mensaje
   */
  function handleKeyPress(e) {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendQuery();
    }
  }

  /**
   * Función para limpiar el chat y empezar una nueva conversación
   */
  function clearChat() {
    setAnswer("");
    setSources([]);
    setQuestion("");
    setConversationId(null);
    setError("");
  }

  return (
    <div className="chat-container">
      {/* Header del chat con título y botones */}
      <div className="chat-header">
        <h1>Asistente RAG Inteligente</h1>
        <div style={{ display: "flex", gap: "10px", justifyContent: "center", marginTop: "15px" }}>
          <button onClick={clearChat} className="btn-secondary" style={{ width: "auto", padding: "8px 16px" }}>
            Nueva Conversación
          </button>
          {onLogout && (
            <button onClick={onLogout} className="btn-secondary" style={{ width: "auto", padding: "8px 16px" }}>
              Cerrar Sesión
            </button>
          )}
        </div>
      </div>

      {/* Área de input para hacer preguntas */}
      <div className="chat-input-area">
        <textarea
          className="chat-input"
          placeholder="Escribe tu pregunta aquí... (presiona Enter para enviar)"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyPress={handleKeyPress}
          disabled={loading}
        />
        <button 
          onClick={handleSendQuery} 
          disabled={loading || !question.trim()} 
          className="chat-send-btn"
        >
          {loading ? (
            <>
              <span className="loading-spinner"></span> Procesando...
            </>
          ) : (
            "Enviar"
          )}
        </button>
      </div>

      {/* Mostrar errores si los hay */}
      {error && (
        <div className="error-message" style={{ marginBottom: "20px" }}>
          {error}
        </div>
      )}

      {/* Área de respuesta del asistente */}
      {(answer || loading) && (
        <div className="chat-response">
          {loading ? (
            <div style={{ textAlign: "center", padding: "20px" }}>
              <span className="loading-spinner" style={{ width: "30px", height: "30px" }}></span>
              <p style={{ marginTop: "15px", color: "rgba(255,255,255,0.7)" }}>
                Analizando documentos y generando respuesta...
              </p>
            </div>
          ) : (
            <>
              {/* Respuesta principal */}
              <h3>Respuesta</h3>
              <p>{answer}</p>

              {/* Lista de fuentes citadas */}
              {sources.length > 0 && (
                <div className="sources-list">
                  <h3>Fuentes Consultadas</h3>
                  <ol>
                    {sources.map((source, index) => (
                      <li key={index}>
                        <div className="source-title">
                          [s{index + 1}] {source.title}
                        </div>
                        {source.page && (
                          <div style={{ fontSize: "12px", color: "rgba(255,255,255,0.6)", marginBottom: "5px" }}>
                            Página {source.page}
                          </div>
                        )}
                        <div className="source-snippet">
                          {source.snippet}
                        </div>
                        {source.uri && (
                          <a 
                            href={source.uri} 
                            target="_blank" 
                            rel="noreferrer" 
                            className="source-link"
                          >
                            Ver documento completo
                          </a>
                        )}
                      </li>
                    ))}
                  </ol>
                </div>
              )}
            </>
          )}
        </div>
      )}

      {/* Elemento para hacer scroll automático */}
      <div ref={endRef} />
    </div>
  );
}
