import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const Chat = ({ onLogout }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [conversations, setConversations] = useState([
    { id: 1, title: 'Conversaci√≥n 1' },
    { id: 2, title: 'Conversaci√≥n 2' },
    { id: 3, title: 'Conversaci√≥n 3' }
  ]);
  const [activeConversation, setActiveConversation] = useState(1);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    const userMessage = { 
      id: Date.now(), 
      text: message, 
      sender: 'user', 
      timestamp: new Date().toLocaleTimeString()
    };
    
    setMessages(prev => [...prev, userMessage]);
    setMessage('');
    setIsLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://127.0.0.1:8000/query/', {
        question: message
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const botMessage = {
        id: Date.now() + 1,
        text: response.data.answer,
        sender: 'bot',
        timestamp: new Date().toLocaleTimeString(),
        sources: response.data.sources || []
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Error al procesar la consulta. Intenta nuevamente.',
        sender: 'bot',
        timestamp: new Date().toLocaleTimeString(),
        isError: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const startNewConversation = () => {
    setMessages([]);
    const newId = Math.max(...conversations.map(c => c.id)) + 1;
    const newConv = { id: newId, title: `Conversaci√≥n ${newId}` };
    setConversations(prev => [newConv, ...prev]);
    setActiveConversation(newId);
  };

  return (
    <div className="chat-container">
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <button className="toggle-sidebar" onClick={() => setSidebarOpen(!sidebarOpen)}>
            {sidebarOpen ? '‚Üê' : '‚Üí'}
          </button>
          {sidebarOpen && (
            <button className="new-chat-btn" onClick={startNewConversation}>
              Nueva Conversaci√≥n
            </button>
          )}
        </div>
        
        {sidebarOpen && (
          <>
            <div className="conversations-section">
              <h4>Chats</h4>
              <div className="conversations-list">
                {conversations.map(conv => (
                  <div 
                    key={conv.id}
                    className={`conversation-item ${activeConversation === conv.id ? 'active' : ''}`}
                    onClick={() => setActiveConversation(conv.id)}
                  >
                    {conv.title}
                  </div>
                ))}
              </div>
            </div>
            
            <div className="sidebar-footer">
              <button className="logout-btn" onClick={onLogout}>
                Cerrar Sesi√≥n
              </button>
            </div>
          </>
        )}
      </div>

      {/* Main Chat Area */}
      <div className="chat-main">
        <div className="chat-header">
          <h1>Asistente RAG Inteligente</h1>
        </div>

        <div className="messages-container">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <div className="welcome-icon">ü§ñ</div>
              <h2>¬°Hola! Soy tu asistente para consultar documentaci√≥n</h2>
              <p>Preg√∫ntame lo que necesites sobre los documentos disponibles</p>
            </div>
          ) : (
            <>
              {messages.map(msg => (
                <div key={msg.id} className={`message ${msg.sender}-message`}>
                  <div className="message-avatar">
                    {msg.sender === 'user' ? 'üë§' : 'ü§ñ'}
                  </div>
                  <div className="message-content">
                    <div className="message-text">
                      {msg.text}
                    </div>
                    {msg.sources && msg.sources.length > 0 && (
                      <div className="message-sources">
                        <div className="sources-header">
                          üìÑ Fuentes consultadas ({msg.sources.length}) ‚ñº
                        </div>
                        <div className="sources-list">
                          {msg.sources.map((source, idx) => (
                            <div key={idx} className="source-item">
                              üîó {source.metadata?.source || `Fuente ${idx + 1}`}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                    <div className="message-time">
                      ‚è∞ {msg.timestamp}
                    </div>
                    {msg.sender === 'bot' && !msg.isError && (
                      <div className="message-feedback">
                        <span>¬øTe ayud√≥ esta respuesta?</span>
                        <button className="feedback-btn">üëç</button>
                        <button className="feedback-btn">üëé</button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
              {isLoading && (
                <div className="message bot-message">
                  <div className="message-avatar">ü§ñ</div>
                  <div className="message-content">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </>
          )}
        </div>

        <div className="input-container">
          <form onSubmit={handleSubmit} className="input-form">
            <div className="input-wrapper">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Ingresa tu consulta"
                className="message-input"
                rows="1"
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSubmit(e);
                  }
                }}
              />
              <button 
                type="submit" 
                className="send-button"
                disabled={isLoading || !message.trim()}
              >
                ‚úàÔ∏è
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Chat;
